dict.string:
DEVICE=$(networksetup -listallhardwareports | grep -A 2 -E "AirPort|Wi-Fi" | grep -m 1 -o -e en[0-9]);

DEVICENAME=$(networksetup -listallhardwareports | grep -Eo "AirPort|Wi-Fi");

echo "<?xml version=\"1.0\"?>"
echo "<items>"

if [[ $(networksetup -getairportpower $DEVICE | grep -c On) == 0 ]]
then
// Turn Wi-Fi On
	echo "<item uid=\"wifi turn on\" arg=\"on\"><title>Turn $DEVICENAME On</title><subtitle></subtitle><icon>icon.png</icon></item>"

else

// IPv4
IPv4=$(networksetup -getinfo $DEVICENAME | grep "IP address" | egrep -o "([0-9]{1,3}\.){3,3}[0-9]{1,3}")
if [[ ! -z "$IPv4" ]]
then echo "<item uid=\"\" arg=\"$IPv4\"><title>$IPv4</title><subtitle>IPv4 address</subtitle><icon>icon.png</icon></item>"
fi

// IPv6
IPv6=$(networksetup -getinfo $DEVICENAME | grep "IPv6 IP address")
IPv6=${IPv6:17}
if [[ "$IPv6" != "none" ]]
then echo "<item uid=\"\" arg=\"$IPv6\"><title>$IPv6</title><subtitle>IPv6 address</subtitle><icon>icon.png</icon></item>"
fi

// Name of current Wi-Fi network
SSID=$(/System/Library/PrivateFrameworks/Apple80211.framework/Versions/Current/Resources/airport -I | grep " SSID" | sed -e 's/^[ \t]*//')
SSID=${SSID:6}

if [[ ! -z "$SSID" ]]
then 

BSSID=$(/System/Library/PrivateFrameworks/Apple80211.framework/Versions/Current/Resources/airport -I | grep "BSSID" | sed -e 's/^[ \t]*//')
BSSID=${BSSID:7}

echo "<item uid=\"\" arg=\"$SSID ($BSSID)\"><title>$SSID ($BSSID)</title><subtitle>Current Wi-Fi Network</subtitle><icon>icon.png</icon></item>"
fi

// Turn Wi-Fi Off
echo "<item uid=\"\" arg=\"off\"><title>Turn $DEVICENAME Off</title><subtitle></subtitle><icon>icon.png</icon></item>"

fi

echo "</items>"
